// import SearchBar from './SearchBar'
import Jobs from './Jobs'
import { ButtonGroup, Dropdown, DropdownButton, Button } from 'react-bootstrap'
import { Container } from 'react-bootstrap'
import { useState, useEffect } from 'react'


const Home = () => {

    const [data, setData] = useState([])
    const [searchInput, setSearchInput] = useState()
    // const [categoryResult, setCategoryResult] = useState()

    console.log("Home", data)

    const fetchJobs = async () => {
        try {
            let resp = await fetch(`https://strive-jobs-api.herokuapp.com/jobs?search=${searchInput}&limit=10`)
            if (resp.ok) {
                let data = await resp.json()
                setData(data.data)
            }
        } catch (error) {
            console.log(error)
        }
    }
    const fetchByCategory = async () => {
        try {
            let resp = await fetch(`https://strive-jobs-api.herokuapp.com/categories?search=${searchInput}&limit=10`)
            if (resp.ok) {
                let data = await resp.json()
                setData(data.data)
            }
        } catch (error) {
            console.log(error)
        }
    }

    const handelSearch = (searchInput) => {
        fetchJobs(searchInput)
        fetchByCategory(searchInput)
    }

    useEffect(() => {
        fetchJobs(searchInput)
        fetchByCategory(searchInput)
    }, [searchInput])

    return (
        <Container className="wrapper">
            <div className="home_items">
                <h1>Search Jobs!</h1>

                <div className="d-flex">
                    <DropdownButton as={ButtonGroup} title="Category" id="md-nested-dropdown"
                        onChange
                    >
                        <Dropdown.Item eventKey="1">Business</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Customer Service</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Data</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Design</Dropdown.Item>
                        <Dropdown.Item eventKey="1">DevOps / Sysadmin</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Finance / Legal</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Human Resources</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Marketing</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Product</Dropdown.Item>
                        <Dropdown.Item eventKey="1">QA</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Sales</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Software Development</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Teaching</Dropdown.Item>
                        <Dropdown.Item eventKey="1">Writing</Dropdown.Item>
                    </DropdownButton>
                    <div className="search_bar_container">
                        <input className="search_bar" placeholder="Search Jobs"
                            value={searchInput}
                            onChange={(e) => setSearchInput(e.target.value)}
                        ></input>
                        <button className="search_btn" style={{ display: "none" }}
                            unSubmit={() => handelSearch(searchInput)}
                        >Search</button>
                    </div>
                </div>

                {
                    data?.map(d => {
                        return <Jobs key={d._id} data={d} />
                    })
                }
            </div>
        </Container>
    )
}

export default Home
